import React, { useState } from "react";
import { Card } from "react-bootstrap";
import { Container } from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import Heart from "react-animated-heart";

const MainPage = (props) => {
    const [isClick, setClick] = useState(false);
    return (
        <div>
            <Container id="ContainerMainPage">
                <Row xs={1} md={2} className="g-4">
                    {Array.from({ length: 4 }).map((_, idx) => (
                        <Col>
                            <Card id="card" style={{ width: '15rem' }}>
                                <Card.Img id="cardimg" variant="top" src="https://bucket-img.tnlmedia.com/cabinet/files/consoles/2/teams/2/2021/9/ykvE14QMdpeEcShwkQr1T5H4IVTHV2dCVsXNCnuG.jpg" />
                                <Card.Title>
                                    <Heart isClick={isClick} onClick={() => this.setClick(!isClick)} />
                                </Card.Title>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </Container>
        </div>
    )
}
export default MainPage;
